function burgerMenu(){const e=document.querySelector(".header__burger"),s=document.querySelector(".header__menu"),c=document.querySelector("body");e.addEventListener("click",(()=>{s.classList.contains("active")?(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked")):(s.classList.add("active"),e.classList.add("active-burger"),c.classList.add("locked"))})),window.addEventListener("resize",(()=>{window.innerWidth>768.98&&(s.classList.remove("active"),e.classList.remove("active-burger"),c.classList.remove("locked"))}))}function fixedNav(){const e=document.querySelector(".header__nav");window.scrollY>=1?e.classList.add("fixed"):e.classList.remove("fixed")}burgerMenu(),window.addEventListener("scroll",fixedNav);